CREATE TABLE `otomobil_db`.`categories` (`id` INT NOT NULL AUTO_INCREMENT , `name` VARCHAR(200) NOT NULL , `image` VARCHAR(250) NULL , `description` TEXT NOT NULL , `status` BIT(1) NOT NULL DEFAULT b'1' , `created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `updated_at` TIMESTAMP on update CURRENT_TIMESTAMP NULL DEFAULT NULL , PRIMARY KEY (`id`)) ENGINE = InnoDB;