CREATE TABLE `otomobil_db`.`users` (`id` INT NOT NULL AUTO_INCREMENT , `name` VARCHAR(150) NOT NULL , `email` VARCHAR(200) NOT NULL , `password` VARCHAR(100) NOT NULL , `role` VARCHAR(50) NOT NULL , `reset_password_token` VARCHAR(10) NULL , `reset_password_token_expired` DATETIME NULL , `created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `updated_at` TIMESTAMP on update CURRENT_TIMESTAMP NULL DEFAULT NULL , PRIMARY KEY (`id`)) ENGINE = InnoDB;